export default NetflixHeader;

const Nav = styled(motion.nav)`
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 14px;
  padding: 10px;
  color: white;
  width: 100vw;
  position: fixed;
`;

const Col = styled.div`
  display: flex;
  align-items: center;
`;

const Logo = styled(motion.svg)`
  width: 95px;
  height: 25px;
`;

const Items = styled.ul`
  display: flex;
  align-items: center;
`;

interface ItemProp {
  match: boolean;
  scroll: number;
}

const Item = styled.li<ItemProp>`
  margin-right: 20px;
  display: flex;
  position: relative;
  color: ${(prop) => prop.match && prop.theme.red};
`;

const Circle = styled(motion.span)`
  position: absolute;
  width: 5px;
  height: 5px;
  border-radius: 5px;
  bottom: -5px;
  left: 0;
  right: 0;
  margin: 0 auto;
  background-color: ${(prop) => prop.theme.red};
`;

const Search = styled.span`
  margin-right: 40px;
  color: white;
  display: flex;
  align-items: center;
  position: relative;
  svg {
    height: 25px;
  }
`;

const Input = styled(motion.input)`
  transform-origin: right center;
  position: absolute;
  left: -175px;
`;
